var modal_box = document.getElementById("modal-box");
var modal_img = document.getElementById("modal-img");

function open_modal_img(src) {
    modal_img.src = src;
    modal_box.style.display = "block";
}

function close_modal_img() {
    modal_box.style.display = "none";
}

// Get all img tags in the document
const imgTags = document.querySelectorAll('img');

// Loop through each img tag and add the onclick event listener
imgTags.forEach(img => {
    if (img.id !== 'modal-img') {
        const imgLink = img.src; // assuming the image link is in the src attribute

        img.onclick = function() {
            open_modal_img(imgLink);
        };
    }
});
